- (SeatType)seatCollectionView:(SeatCollectionView *)colelctionView seatTypeforIndexPath:(NSIndexPath *)indexPath {
	switch (indexPath.section) {
  	case 0:
			return SeatTypeSleeper;
		case 1:
			return SeatTypeSeater;
		case 2:
			if (indexPath.item == 0) {
				return SeatTypeSleeper;
			}else {
				return SeatTypeSeater;
			}
		case 3:
			return SeatTypeSleeper;
		case 4:
			return SeatTypeSleeper;

  default:
			return SeatTypeNone;
		}
}

